import React, { useEffect, useState } from 'react'
import axios from 'axios'

export default function Home(props) {
      const [data,setData]=useState([])

      function getData(){
            axios.get(`https://newsapi.org/v2/everything?q=${props.q}&sortBy=publishedAt&apiKey=d3dd4cebc9dd453b84a87f668b6d2109`).then(res=>{
                  // console.log("data fetch successfully")
                  setData(res.data.articles)
            }).catch(error=>{
                  console.log(error)
            })
      }

      useEffect(()=>{
            getData();
      },[props.q])

  return (
    <>
