import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Home(props) {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get(
        `https://newsapi.org/v2/everything?q=${props.q}&sortBy=publishedAt&apiKey=d3dd4cebc9dd453b84a87f668b6d2109`
      )
      .then((res) => {
        console.log(res.data.articles)
        setData(res.data.articles);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  useEffect(() => {
    getData();
  }, [props.q]);

  return (
    <>
      <div className="container">
        <div className="row mt-5">
          {data.map((item, index) => {
            return (
              <div className="col-xl-2 col-lg-3 col-md-4 col-sm-6 col-12 mb-3">
                <div className="card">
                  <img
                    src={data.urlToImage}
                    height="200px"
                    className="card-img-top"
                    alt="..."
                  />
                  <div className="card-body">
                    <h5 className="card-title">
                    {}
                    </h5>
                    <hr />
                    <div className="d-flex justify-content-between">
                      <p className="date-and-source">fgf</p>
                      <p className="date-and-source">
                        fgfgfg
                      </p>
                    </div>
                    <hr />
                    <p className="card-text">
                      fgffg
                    </p>
                    <a
                      href="#"
                      className="btn background text-light w-100 btn-sm"
                      target="_blank"
                      rel="noreferrer"
                    >
                      Read Full Article
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}
